#define last last2

using namespace std;

int num[200005],pre[200005],nxt[200005],len[200005];
bool col[200005],vis[200005];

queue <int> que[2],q2;
int kind[200005];

bool calc(int n,int m) {
  memset(pre,0,sizeof(pre));
  memset(nxt,0,sizeof(nxt));
  memset(len,0,sizeof(len));
  memset(vis,0,sizeof(vis));
  int cur=0;
  while (!que[cur].empty()) que[cur].pop();
  for(int i=1;i<=2*n-1;i++) col[i]=(num[i]>m);
  bool ans=col[1];
  int tot=0,last=1;
  for(int i=2;i<=2*n-1;i++)
    if (col[i]!=col[i-1]) {
    	len[++tot]=i-last;
    	last=i;
	}
  len[++tot]=2*n-last;
  for(int i=1;i<=tot;i++) {
  	pre[i]=i-1;
  	if (i<tot) nxt[i]=i+1; else nxt[i]=0;
  	if (len[i]==1) que[cur].push(i);
  }
  int l=1,r=tot;
  for(int i=1;i<n;i++) {
  	cur^=1;
  	while (!que[cur].empty()) que[cur].pop();
  	while (!q2.empty()) q2.pop();
  	if (len[l]==1&&len[nxt[l]]>1) ans^=1;
  	while (!que[cur^1].empty()) {
  		int x=que[cur^1].front();que[cur^1].pop();
  		if (x==l||x==r||len[x]>1||vis[x]) continue;
  		if (len[pre[x]]>1&&len[nxt[x]]>1) {
		    vis[x]=1;q2.push(x);
		    kind[x]=1;
		  }
		else if (len[pre[x]]>1) {
			vis[x]=1;q2.push(x);
			kind[x]=2;
		}
		else if (len[nxt[x]]>1) {
			vis[x]=1;q2.push(x);
			kind[x]=3;
		}
	  }
  	while (!q2.empty()) {
  		int x=q2.front();q2.pop();
  		if (kind[x]==1) {
  			int p=pre[x],q=nxt[x];
  			nxt[p]=nxt[q];
  			pre[nxt[q]]=p;
  			len[p]=len[p]+len[q]+len[x];
  			if (len[pre[p]]==1) que[cur].push(pre[p]);
  			if (len[nxt[q]]==1) que[cur].push(nxt[q]);
		  }
		else if (kind[x]==2) {
			int p=pre[x],q=nxt[x];
			nxt[p]=q;
			pre[q]=p;
			len[p]=len[p]+len[x];
			if (len[pre[p]]==1) que[cur].push(pre[p]);
			if (len[q]==1) que[cur].push(q);
		}
		else if (kind[x]==3) {
			int p=pre[x],q=nxt[x];
			pre[q]=p;
			nxt[p]=q;
			len[q]=len[q]+len[x];
			if (len[nxt[q]]==1) que[cur].push(nxt[q]);
			if (len[p]==1) que[cur].push(p);
		}
	  }
	len[l]--;len[r]--;
	if (!len[l]) {
		int q=nxt[l];
		pre[q]=nxt[l]=0;
		l=q;
	}
	if (!len[r]) {
		int q=pre[r];
		nxt[q]=pre[r]=0;
		r=q;
	}
  }
  return ans;
}

int main() {
  int n;
  scanf("%d",&n);
  for(int i=1;i<=2*n-1;i++) scanf("%d",&num[i]);
  int l=1,r=2*n-1;
  while (l<r) {
  	int m=((l+r)>>1);
  	if (calc(n,m)) l=m+1; else r=m;
  }
  printf("%d\n",l);
  return 0;
}

