		}
	}
	int itr[2] = {};
	int ans[2] = {};
	for (int i = geko.size()/2; i < geko.size()-1; ++i) {
		if (newer[i] == newer[i + 1]) {
			itr[0] = i;
			ans[0] = newer[i];
