		}
	}
	for (int i = geko.size()/2; i >= 1; --i) {
		if (newer[i] == newer[i - 1]) {
			itr[1] = i;
			ans[1] = newer[i];
			break;
		}
	}
	if (abs((int)geko.size()/2 - itr[0]) > abs((int)geko.size()/2 - itr[1])) {
		return ans[1];
	}
	else {
		return ans[0];
	}
