  // printf("x: %d\nb: ", x);
  REP(i, K) b[i] = a[i] >= x ? 1 : 0;
  // DUMP(b, K);
  int res;
  int i;
  int lv = 0, lb = -1, rv = 0, rb = -1;
  for (i = M - 1; i >= 0 && b[i] != b[i + 1]; i--) lv++;
  if (i >= 0) lb = b[i];
  for (i = M + 1; i < K && b[i] != b[i - 1]; i++) rv++;
  if (i < K) rb = b[i];
  if (lb < 0 && rb < 0) {
    res = (N % 2 > 0 ? b[M] : 1 - b[M]);
  } else if (lv == 0 || rv == 0) {
    res = b[M];
  } else if (lv < rv) {
    res = lb;
  } else {
    res = rb;
  }
  // printf("lv: %d, lb: %d, rv: %d, rb: %d, res: %d\n", lv, lb, rv, rb, res);
  return res == 1;
