  while (hi - lo > 1) {
    // printf("hi: %d, lo :%d\n", hi, lo);
    int mi = (hi + lo) / 2;
    if (C(mi)) lo = mi;
    else hi = mi;
  }
  // printf("hi: %d, lo :%d\n", hi, lo);
  printf("%d\n", lo);
}

void input() {
  scanf("%d", &N);
  K = 2 * N - 1;
  M = N - 1;
  REP(i, K) scanf("%d", a + i);
}

