bool ok(int q) {
	int at=(m-1)/2,l=at,r=at;
	while(l-1>=0&&(x[l-1]>=q)!=(x[l]>=q)) --l; int zl=x[l]>=q?1:0;
	while(r+1< m&&(x[r+1]>=q)!=(x[r]>=q)) ++r; int zr=x[r]>=q?1:0;
