int N;
vector<int> A;

bool f(int m) {
    bitset<10> bits;
    for (int i : range(2 * N - 1)) {
        bits[i] = A[i] >= m;
    }

    for (int _ : range(N - 1)) {
        unused_var(_);
        auto bits1 = bits ^ (bits >> 1) ^ (bits >> 2);
        bits1.flip();
        auto bits2 = bits & (bits >> 1) & (bits >> 2);
        bits.flip();
        auto bits3 = bits & (bits >> 1) & (bits >> 2);
        bits       = bits1 ^ bits2 ^ bits3;
        // 110 -> 100
        // 111 -> 010
        // 100 -> 000
        // 000 -> 101
    }
    return bits[0];
}

int naive() {
    int ok = -1;
    int ng = 2 * N - 1;
    while (abs(ok - ng) > 1) {
        const int mid = (ok + ng) / 2;
        if (f(mid)) {
            ok = mid;
        }
        else {
            ng = mid;
        }
    }
    return ok;
}

