	apadd(ans,sub(c(n-1,k-1),c(n-3,k-1)));//000 & 100 & 001
	for(int t=n-3;t>=2;t-=2){//00 00
		apadd(ans,mul(intcnt(2,m-1,m+1-t,n-1-t),c(n-t-3,k-t/2-3)));
	}
	{//00 11
		int cur=0;
		for(int t=n-4;t>=3;t-=2){
			apadd(cur,mul(intcnt(3,m-1,(n-t)/2+1,n-1-t),c(n-t-3,k-(t+1)/2-1)));
		}
		apadd(ans,mul(cur,2));
	}
	{//00 
		int cur=0;
		for(int i=2;i<m;i++){
			apadd(cur,c(i-2,k-(n-i)/2-2));
		}
		apadd(ans,mul(cur,2));
	}
	if(k==n-k+1){//101
		apadd(ans,1);
	}
	apmul(ans,mul(fac[k],fac[n-k]));
	return ans;
}
int main(){
	int n=ni,k=ni;
	{//get fac
		fac[0]=1;
		for(int i=1;i<=n;i++){
			fac[i]=mul(fac[i-1],i);
		}
		invfac[n]=inv(fac[n]);
		for(int i=n;i>=1;i--){
			invfac[i-1]=mul(invfac[i],i);
		}
	}
	if(n==1){
		puts("1");
	}else if(k==1||k==n){
		puts("0");
	}else{
		printf("%d\n",sub(work(n,k),work(n,k-1)));
	}
}

