#include <bits/stdc++.h>
using namespace std;

#define MAX_N 100005

int N;
vector<int> A;

int med(int&a ,int&b ,int&c)
{
    if(min(a ,c) <= b && b <= max(a ,c)) return b;
    if(min(b ,c) <= a && a <= max(b ,c)) return a;
    if(min(b ,a) <= c && c <= max(b ,a)) return c;
}

int Solve(vector<int>A)
{
    vector <int> B = A;

    for(int j=1; j<N; j++ ,swap(A ,B))
    for(int i=j; i<((int)A.size())-j; i++)
        B[i] = med(A[i-1] ,A[i] ,A[i+1]);

    return A[N-1];
}

int main()
{
    scanf("%d",&N); A.resize(2*N-1);
    for(int i=0; i<2*N-1; i++)
        scanf("%d",&A[i]);

    if(N < 1005)
        cout << Solve(A) << endl;
    else
    {
        int st = N-500 ,en = N+500;
        vector <int> B;
        for(int i=st; i<=en; i++)
            B.push_back(A[i]); N = (B.size()+1)/2;
        cout << Solve(B) << endl;
    }
}

