    if (b[i] == b[i - 1]) {
      rp = i - n;
      break;
    }
  }

  int lp = 0;
  for (int i = n - 1; i; -- i) {
    if (b[i] == b[i + 1]) {
      lp = n - i;
      break;
    }
  }

  if (!rp && !lp) return b[1];

  if (!rp) return b[n - lp];
  if (!lp) return b[n + rp];

  if (rp == lp) return b[n - lp];

  // printf("%d %d\n", lp, rp);
  if (rp < lp) {
    return b[n + rp];
  } else {
    return b[n - lp];
  }

}

