bool check(int x){
	L = 1, R = 2*n+1; 
  for(int i=1;i<=n*2+1;i++)
    B[i] = (A[i]>=x);

  for(int i=1;i<n+1;i++)
    if(B[i]==B[i+1]){
    	L = i+1;
    }

  for(int i=2*n+1;i>n+1;i--)
    if(B[i]==B[i-1]){
    	R = i-1;
    }

  if((n+1-L)<(R-n-1)){
  	return B[L] == 1;
  } else {
  	return B[R] == 1;
  }
}

