int sol(int val, vel &a) {
	int m = a.size();
	vel b(m + 2);
	b[0] = a[0]; b[m + 1] = a[m - 1];
	rep(i, m) { b[i + 1] = a[i]; }
	m += 2;
	rep(i,m){
		if (b[i] <= val) { b[i] = 0; }
		else { b[i] = 1; }
	}
	int n = m / 2;
	if (b[n - 1] == b[n] || b[n + 1] == b[n]) { return b[n]; }
	pin lef;
	rep(i, n) {
		if (b[i] == b[i + 1]) { lef = mkp(i, b[i]); }
	}
	pin ri;
	for (int i = m - 1; i > n; i--) {
		if (b[i] == b[i - 1]) { ri = mkp(i, b[i]); }
	}
	if (lef.first + ri.first >= 2 * n) { return lef.second; }
	else { return ri.second; }
}
