const int N = 3e6 + 10;

int n, h, l, r, res;
int a[N], c[N], d[N];

inline int check(int x) {
	for (int i = 1; i <= n; ++i)	c[i] = (a[i] >= x);
	int L = 1, R = n, mid = (L + R) >> 1;
	for (int i = 1; i < h; ++i) {
		if (c[mid - 1] + c[mid] == 0 || c[mid] + c[mid + 1] == 0)	return 0;
		if (c[mid - 1] + c[mid] == 2 || c[mid] + c[mid + 1] == 2)	return 1;
		if (R - L > 2 && !c[mid] && (c[mid - 1] + c[mid + 1] + c[mid + 2] == 3 || c[mid - 2] + c[mid - 1] + c[mid + 1] == 3))	return 1;
		if (R - L > 2 && c[mid] && (c[mid - 1] + c[mid + 1] + c[mid + 2] == 0 || c[mid - 2] + c[mid - 1] + c[mid + 1] == 0))	return 0;
		L--, R--;
		for (int j = L; j <= R; ++j)	d[j] = ((c[j - 1] + c[j] + c[j + 1]) >= 2);
		for (int j = L; j <= R; ++j)	c[j] = d[j];
	}
	return c[mid];
}

