int n;
int t[200010], p[200010];

bool check(int x) {
    for (int i = 1; i < 2 * n; ++ i) t[i] = (p[i] <= x ? 0 : 1);
    int flag1 = 0, flag2 = 0, f = 0;
    for (int i = 2; i < 2 * n; ++ i) if (t[i] == t[i - 1]) { f = 1; break ; }
    if (f) {
        if (n % 2 == 0) t[n] ^= 1;
        return t[n];
    }
    flag1 = 1, flag2 = 2 * n - 1;
    int cnt1 = -1, cnt2 = -1;
    if (t[n] == t[n - 1] || t[n] == t[n + 1]) return t[n];
    for (int i = n - 1; i > 1; -- i) if (t[i] == t[i - 1]) { flag1 = i; cnt1 = t[i]; break ; }
    for (int i = n + 1; i < 2 * n - 1; ++ i) if (t[i] == t[i + 1]) { flag1 = i; cnt2 = t[i]; break ; }
    return flag1 < flag2 ? cnt1 : cnt2;
}

