			rig = i-1;
		}
	}
	{
		int cnt = 0;
		int ich = rig;
		while (ar[ich]&&ich>=0){
			cnt++;
			ich -= 2;
		}
		if (ich < lef){
			return true;
		}
		int len = rig - ich+1;
		len /= 2;
		if (rig - lef + 1 <= u){
			return true;
		}
	}
	{
		int cnt = 0;
		int ich = lef;
		while (ar[ich] && ich < ar.size()){
			cnt++;
			ich += 2;
		}
		int len = ich - lef - 1;
		len /= 2;
		if (lef + len-1 >= u){
			return true;
		}
	}
