bool f(int x , int n)
{
    for(int i=1 ; i<=n ; i++)
    {
        if(a[i]<x) b[i] = 0 ;
        else b[i] = 1 ;
    }

    int m = (n+1)/2 ;

    if(b[m] == b[m+1] || b[m] == b[m-1]) return b[m] ;

    bool fl = 0 ;
    for(int i=1 ; i<n ; i++)
    {
        if(b[i] == b[i+1]) fl = 1 ;
    }

    if(fl==0)
    {
        if( (m-1)%2==1 ) return (b[m]^1) ;
        else return b[m] ;
    }

    int r = 2*n , l = 2*n ;

    for(int i=m+1 , j=1 ; i<n ; i++,j++)
    {
        if( b[i]==b[i+1] )
        {
            r = j ;
            break ;
        }
    }

    for(int i=m-1,j=1 ; i>1 ; i--,j++)
    {
        if(b[i] == b[i-1] )
        {
            l = j ;
            break ;
        }
    }

    if(l<r) return b[m-l] ;
    else return b[m+r] ;
}

