int main(){cin.tie(0);ios::sync_with_stdio(false);
	int N;cin>>N;
	int M=2*N-1;
	vi A(M);cin>>A;
	int ok=1,ng=M;
	while(ng-ok>1){
		int t=ok+ng>>1;
		vb B(M);
		fr(i,M){
			B[i]=A[i]>=t;
		}
		int j=-1;
		fr(i,M){
			if((i==0||B[i-1]!=B[i])&&(i==M-1||B[i]!=B[i+1])){
				if(j<0){
					j=i;
				}
			}else{
				if(j>=0){
					if(j<=N-1&&N-1<i){
						if(i-j&1){
							B[N-1]=!B[j];
						}else{
							if(N-1>=j+i>>1){
								B[N-1]=B[j];
							}else{
								B[N-1]=!B[j];
							}
						}
					}
					j=-1;
				}
			}
		}
		if(j==0){
			B[N-1]=B[j];
		}elsif(0<j&&j<=N-1){
			B[N-1]=!B[j];
		}
		if(B[N-1]){
			ok=t;
		}else{
			ng=t;
		}
	}
	print(ok);
	return 0;
}

