inline int read() {
	int x = 0, f = 1;
	char ch = getchar();
	while(!isdigit(ch)) ch = getchar();
	while(isdigit(ch)) {
		x = (x << 3) + (x << 1) + (ch & 15);
		ch = getchar();
	}
	return x * f;
}

inline int two(int x,int y,int z) {
	if((x >= y && x <= z) || (x >= z && x <= y)) return x;
	if((y >= x && y <= z) || (y >= z && y <= x)) return y;
	if((z >= x && z <= y) || (z >= y && z <= x)) return z;
}

inline void write(int x) {
	int y = 10,len = 1;
	while(y <= x) {
		y = (y << 3) + (y << 1);
		len++;
	}
	while(len--) {
		y /= 10;
		putchar(x / y + '0');
		x %= y;
	}
}

int main()
{
//	freopen("pyramid.in","r",stdin);
