	n = read();
	int len = (n << 1) - 1;
	for(int i = 1; i <= (n << 1) - 1; i++)
		a[i] = read();
	for(int i = 1; i <= n - 1; i++) {
		for(int j = 1; j <= len; j++) {
			a[j] = two(a[j],a[j + 1],a[j + 2]);
		}
		len -= 2;
	}
	write(a[1]);
	printf("\n");
	return 0;
}

