int judge(int avr){
	b[0]=b[len+1]=-1;
	for(int i=1;i<=len;i++){
		if(a[i]>=avr) b[i]=1;
		else b[i]=0;
		if(b[i]==b[i-1]) cnt[i]=cnt[i-1]+1;
		else cnt[i]=1;
	}
	for(int i=len;i>=1;i--){
		if(b[i]==b[i+1]) sum[i]=sum[i+1]+1;
		else sum[i]=1;
	}
	for(int i=0;i<n;i++){
		if(cnt[n+i]+sum[n+i]-1>=2) return b[n+i];
		if(cnt[n-i]+sum[n+i]-1>=2) return b[n-i];
	}
	return b[1];
}
