int solve(const int N, const vector<int>&a) {
	//a01
	const int c = N - 1;//center

	if (a[c] == a[c - 1] || a[c] == a[c + 1]) {
		return a[c];
	}
	int L = -1, R = 2 * N;
	for (int i = c - 1; 0 <= i; i--) {
		if (a[i] == a[i + 1]) {
			L = i;
			break;
		}
	}
	for (int i = c + 1; i < 2 * N - 1; i++) {
		if (a[i] == a[i - 1]) {
			R = i;
			break;
		}
	}

	if (L == -1 && R == 2 * N) {
		return (N % 2) ? a[c] : (a[c] ^ 1);
	}
	if (L == -1) {
		return a[R];
	}
	if (R == 2 * N) {
		return a[L];
	}
	if (a[L] == a[R]) {
		return a[L];
	}

	int Ldist = c - L;
	int Rdist = R - c;
	if (Ldist < Rdist)return a[L];
	return a[R];
}

