	int lb = 0, ub = 2 * N;//[lb,ub)
	while (lb + 1 < ub) {
		const int med = (lb + ub) / 2;
		rep(i, 2 * N - 1)bin[i] = a[i] < med ? 0 : 1;
		const int res = solve(N, bin);
		if (res == 0)ub = med;
		else lb = med;
	}

	cout << lb << endl;

	return 0;
}

