int N;
int a[200000];
int t[200000];

bool f(int s) {
  for (int i=0; i<2*N-1; i++) t[i] = a[i] >= s ? 1 : 0;

  int g = N-1;
  if (t[g] == t[g-1] || t[g] == t[g+1]) return t[g];
  int l = t[g-1];
  int r = t[g+1];
  for (int i=2; i<N-1; i++) {
    if (t[g-i] == l) {
      return i < N ? l : (t[g] + N + 1) % 2;
    }
    if (t[g+i] == r) {
      return i < N ? r : (t[g] + N + 1) % 2;
    }
    l = t[g-i];
    r = t[g+i];
  }
  return (t[g] + N + 1) % 2;
}

