set<pii> st;

bool ok(int x)
{
	st.clear();

	rep(i, N*2-1) {
		s[i] = (a[i] >= x);
	}

	for (int i = 1; i < N*2-1; ++i) {
		f[i] = (s[i] != s[i-1] && s[i] != s[i+1]);
	}

	int l = -1;
	for (int i = 1; i < N*2-1; ++i) {
		//if (x == 4) cout << i << " " << l << endl;
		if (f[i]) {
			if (l == -1) {
				l = i;
			}
		} else {
			if (l != -1) {
				st.insert(mp(l, i-1));
			}
			l = -1;
		}
	}
	if (l != -1) st.insert(mp(l, N*2-2));
	if (x == 4) {
		for (pii p:st) {
		//	cout << p.fi << " " << p.se << endl;
		}
	}

	for (int i = 1; i < N; ++i) {
		set<pii> nx;

		//if (x == 4) cout<<"NOW=" <<i<<endl;
		for (pii p:st) {
			//cout << p.fi << " " << p.se << endl;
		}
		for (auto it = st.begin(); it != st.end(); ) {
			int l = it->fi, r = it->se;
			bool sl = s[l] ^ (i & 1);
			bool sr = s[r] ^ (i & 1);
			if (sl == s[l-1]) {
				s[l] = s[l-1];
				++l;
			}
			if (sr == s[r+1]) {
				s[r] = s[r+1];
				--r;
			}
			st.erase(it++);

			if (l <= r) {
				if (l == i) {
					s[l] = s[l] ^ (i & 1);
					++l;
				}

				if (l <= r && r == N-1-i) {
					s[r] = s[r] ^ (i & 1);
					--r;
				}

				nx.insert(mp(l, r));
			}
		}

		for (pii p : nx) {
			st.insert(p);
		}
	}

	return s[N-1];
}

