int n;
VI a;
VI b;

bool can(int x)
{
	a = b;
	FOR(i, 0, n)
		if (a[i] < x)
			a[i] = 0;
		else
			a[i] = 1;

	if (a[n / 2] == a[n / 2 - 1])
		return a[n/2];

	VI ok;
	FOR(i, 0, n - 1)
		if (a[i] == a[i + 1])
			ok.push_back(i);

	if (SZ(ok) == 0)
		return a[0];

	VI dist(SZ(ok));
	FOR(i, 0, SZ(ok))
		if (ok[i] >= n / 2)
			dist[i] = ok[i] - n / 2;
		else
			dist[i] = n / 2 - 1 - ok[i];

	int best = dist[0];
	FOR(i, 1, SZ(ok))
		best = min(best, dist[i]);

	FOR(i, 0, SZ(ok))
		if (best == dist[i])
			return a[ok[i]];

	throw - 1;
}

