                ++right;
            }
            if ((left == 0 && right == n - 1) || B[left] != B[right]) {
                xa = B[n - 1] ^ (n & 1);
            } else {
                xa = B[left];
            }
        }
        if (xa) {
            ans += step;
        }
    }
    ans++;
