int N;
vector<int> a;
bool is_eq_greater(int val)
{
    vector<int> v(2 * N - 1);
    REP(i, 2 * N - 1) v[i] = (a[i] >= val);
    if (!v[N - 1] and !v[N]) return false;
    if (!v[N - 1] and !v[N - 2]) return false;
    int e = 0;
    REP(k, N - 1)
    {
        if (v[N - 1 + k] + v[N - 1 + k + 1] != 2) { e++; break; }
        if (v[N - 1 + k] + v[N - 1 + k + 1] == 0) break;
        if (k == N - 2) e++;
    }
    REP(k, N - 1)
    {
        if (v[N - 1 - k] + v[N - 1 - k - 1] == 2) { e++; break; }
        if (v[N - 1 - k] + v[N - 1 - k - 1] == 0) break;
        if (k == N - 2) e++;
    }
    return e == 2;
}


int main()
{
    cin >> N;
    a.resize(2 * N - 1);
    cin >> a;

    int L = 1, R = N + 1; // [L, R)
    while (R - L > 1)
    {
        int C = (L + R) / 2;
        (is_eq_greater(C) ? L : R) = C;
    }
    cout << L << endl;
}

