	int i, j;
	for(i = 1 ; i <= n * 2 - 1 ; ++i)
		b[i] = a[i] >= k;
	if(b[n - 1] == b[n] || b[n] == b[n + 1])
		return b[n];
	i = n - 1, j = n + 1;
	while(i){
		if(b[i] == b[i + 1]) return b[i];
		if(b[j] == b[j - 1]) return b[j];
		--i, ++j;
	}
