bool ck(){
	fp(i,1,n)b[i]=a[i]>=mid;
	R int top=0;bool fl1=1,fl2=1;
	fp(i,1,n)fl1&=(b[i]==(i&1)),fl2&=(b[i]!=(i&1));
	if(fl1)return true;if(fl2)return false;
	for(R int l=1,r=2;r<=n;l=r++){
