	register char ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')
		{
			flag=0;
		}
		ch=getchar();
	}
	while(ch<='9'&&ch>='0')
	{
		res=(res<<3)+(res<<1)+(ch^48);
		ch=getchar();
	}
	return flag?res:-res;
}

