	while (!isdigit(c))
		c = getchar();
	while (isdigit(c)) {
		x = (x << 3) + (x << 1) + c - '0';
		c = getchar();
	}
	return x;
}

bool interval(int l, int r) {
	for (int i = l + 1; i <= r; ++i)
		if (b[i] == b[i - 1])
			return false;
	return b[l];
}

bool check(int w) {
	bool flag1, flag2, flag3;
	for (int i = 1; i <= 2 * n - 1; ++i)
		b[i] = a[i] >= w;
	/*for (int i = 1; i <= 2 * n - 1; ++i)
		cout << b[i] << " ";
	cout << endl;*/
	flag1 = interval(1, 2 * (n - 1) - 1) || (b[n - 2] && b[n - 1]) || (b[n - 1] && b[n]);
	flag2 = interval(2, 2 * n - 2) || (b[n - 1] && b[n]) || (b[n] && b[n + 1]);
	flag3 = interval(3, 2 * n - 1) || (b[n] && b[n + 1]) || (b[n + 1] && b[n + 2]);
	return (flag1 + flag2 + flag3) >= 2;
}

int solve() {
	int l = 1, r = maxi, res = -1;
	while (l <= r) {
		int mid = (l + r) >> 1;
		if (check(mid)) {
			res = mid;
			l = mid + 1;
		}
		else 
			r = mid - 1;
	}
	return res;
}

