{
	scanf("%d", &N);

	for (int i = 0; i < 2*N-1; i++) scanf("%d", &input[i]);

	int low = 1, high = 2 * N - 1;

	while (low <= high)
	{
		if (high - low > 1)
		{
			int mid = (low + high) / 2;

			if (last(mid) == 1) low = mid;
			else high = mid - 1;
		}
		else
		{
			if (high == low) break;
			else
			{
				if (last(high) == 1) low = high;
				break;
			}
		}
	}

	printf("%d\n", low);

	return 0;
}

