constexpr int MAXN = 262144;

int n,m;
int a[MAXN];

bool Check( int lim ){
	static int a[MAXN];
	For(i,m) a[i] = ::a[i]>=lim;
	static bool isContinue[MAXN];
	For(i,m) isContinue[i] = ( i > 1 and a[i] == a[i-1] ) or ( i < m and a[i] == a[i+1] );

	if(isContinue[n])
		return a[n];

	int l = n, r = n;
	while( l >= 1 and !isContinue[l] ) --l;
	while( r <= m and !isContinue[r] ) ++r;
	++l; --r;
	if( l == r ) return !a[n];

	if( l == 1 and r == m ) return (n&1^1) ^ a[n];
	if( n-l == r-n ) return a[l-1];
	return n-l < r-n ? a[l-1] : a[r+1];
}

int BinaryLift(){
	int ans = 1;
	fOR0(i,19){
		int vAns = ans + (1<<i);
		if( vAns <= m and Check(vAns) )
			ans = vAns;
	}
	return ans;
}

int main(){
	FILEIO();

	Read(n);
	m = n+n-1;
	Read(a,1,m);

	int ans = BinaryLift();
	cout << ans << endl;

	END();
}

