using namespace std;
template <class T> inline void setmax(T & a, T const & b) { a = max(a, b); }
template <class T> inline void setmin(T & a, T const & b) { a = min(a, b); }

int median(int a, int b, int c) {
    if (a > b) swap(a, b);
    return max(a, min(b, c));
}
int main() {
    // input
    int n; scanf("%d", &n);
    vector<int> a(2 * n - 1);
    repeat (i, 2 * n - 1) {
        scanf("%d", &a[i]);
    }
    // solve
    vector<int> b(2 * n - 1);
    int l = 0;
    int r = 2 * n - 1;
    repeat (i, n - 1) {
        -- l;
        setmax(l, i + 1);
        ++ r;
        setmin(r, (2 * n - 1) - (i + 1));
        repeat_from (j, l, r) {
            b[j] = median(a[j - 1], a[j], a[j + 1]);
        }
        repeat_from (j, n - 1, r) if (b[j - 1] == b[j]) r = j;
        repeat_from_reverse (j, l, n - 1) if (b[j] == b[j + 1]) l = j;
        while (l <= n - 1 and b[l] == a[l]) ++ l;
        while (n - 1 < r and b[r - 1] == a[r - 1]) -- r;
        a.swap(b);
    }
    // output
    printf("%d\n", a[n - 1]);
    return 0;
}

