bool ok(int k)	{
	for (int i = 1; i < n + n; i++)
	{
		b[i] = (a[i] >= k);
	}
	for (int i = 0; i < n; i++)
	{
		if(b[n + i] == b[n + i + 1])
			return b[n + i];
		if(b[n - i] == b[n - i - 1])
			return b[n - i];
	}
