# define NR 201000

bool a[NR];
int w[NR], n, m;

bool calc_top () {
	int p = 0;

	//REP (i, n) printf ("%d ", a[i]); puts (" Beg");

	FOR (i, 2, n) {
		if (a[i] == a[i - 1]) {
			int l = p + 1, r = i - 2;
			if (l <= r) {
				int len = r - l + 1;
				if (len % 2 == 0) {
					REP_0 (j, len) {
						if (j < len / 2) a[l + j] = a[l - 1];
						else a[l + j] = a[r + 1];
					}
				} else FOR (j, l, r) a[j] = a[r + 1];
			}
			p = i;
		}
	}

	int l = p + 1, r = len;
	if (len % 2 == 0) {
		REP_0 (j, len) {
			if (j < len / 2) a[l + j] = a[l - 1];
			else a[l + j] = a[r + 1];
		}
	} else FOR (j, l, r) a[j] = a[l - 1];

	//REP (i, n) printf ("%d ", a[i]); puts (" End");

	return p == 0 ? a[1] : a[m];
}

