		int mid = (l + r + 1) / 2;
		REP (i, n) a[i] = (w[i] >= mid);
		//printf ("!%d\n", mid);
		if (calc_top ()) l = mid;
		else r = mid - 1;
	}

	printf ("%d\n", l);
	return 0;
}

