            if(A[i] <= m && A[i+1] <= m) yl = i-N;
            if(A[i] > m && A[i+1] > m) yl = i-N;
            if(yl != INF) break;
        }
        char res;
        if(min(xl, yl) == INF) {
            if((A[N] <= m) ^ (N%2)) res = 'U';
            else res = 'D';
        }
        else if(xl < yl) {
            if(A[N-xl] <= m) res = 'D';
            else res = 'U';
        }
        else {
            if(A[N+yl] <= m) res = 'D';
            else res = 'U';
        }
        if(res == 'D') r = m-1;
        else l = m+1;
    }
    printf("%d", r+1);
    return 0;
}

