bool ok(const vector<int>& muv, int v) {
	vector<bool> mub(muv.size());
	FOR(i, 0, muv.size()) mub[i] = (muv[i] >= v);
	int mid = mub.size() / 2;
	int d = mub.size()-1;
	FOR(i, mid, mub.size()-1) {
		if (mub[i] == mub[i + 1]) {
			d = i;
			break;
		}
	}
	RFOR(i, 1, mid+1) {
		if (mub[i] == mub[i - 1]) {
			d = (d - mid >  mid - i ? i : d);
			break;
		}
	}
	return mub[d];
}


