int check(int mid,vi a){
    int m=SZ(a);
    for(int i=0;i<m;i++){
        if(a[i]<=mid)
            a[i]=0;
        else
            a[i]=1;
    }
    int n=m/2;
    int lst=a[n];
    int l=n-1,r=n+1;
    while(l>=0&&r<m){
        if(a[l]==lst||a[r]==lst)
            return lst;
        lst=a[l];
        l--;
        r++;
    }
    return a[0];
}

