bool Possible(const vector<int>& a, int v) {
    vector<int> b(a.size());
    for (int i = 0; i < a.size(); ++i) b[i] = (int)(a[i] >= v);
    int n = (a.size() + 1) / 2;
    if (b[n - 1] == b[n - 2] || b[n - 1] == b[n]) {
        return b[n - 1];
    }
    int l = n - 2, r = n;
    while (l > 0 && b[l - 1] != b[l]) --l;
    while (r < 2 * n - 2 && b[r] != b[r + 1]) ++r;
    if ((n - 1 - l) == (r - n + 1)) {
        if (l == 0) {
            return (b[n - 1] + n - 1) % 2;
        } else {
            return b[l];
        }
    } else {
        if ((n - 1 - l) < (r - n + 1)) return b[l];
        else return b[r];
    }
}

