  for (int i = 1; i <= sz; ++ i) {
    b[i] = a[i] <= x;
  }

  int rp = 0;
  for (int i = n + 1; i <= sz; ++ i) {
    if (b[i] == b[i - 1]) {
      rp = i - n;
      break;
    }
  }

  int lp = 0;
  for (int i = n - 1; i; -- i) {
    if (b[i] == b[i + 1]) {
      lp = n - i;
    }
  }

  if (!rp && !lp) return b[1];

  if (!rp) return b[n - lp];
  if (!lp) return b[n + rp];

  if (rp == lp) return b[n - lp];

  if (rp < lp) {
    return b[n + rp];
  } else {
    return b[n - lp];
  }

}

