namespace math {

    /**
     * @brief a
     * @param a int64
     * @param b int64
     * @return int64 
     */
    int64_t gcd(int64_t a, int64_t b) { return (b == 0) ? a : gcd(b, a % b); }

    /**
     * @brief a
     * @param a int64
     * @param b int64
     * @return int64 
     */
    int64_t lcm(int64_t a, int64_t b) { return (a / gcd(a, b)) * b; }


    /**
     * @brief 
     * @details ax + by = gx,y
     * @param a 
     * @param b 
     * @param x 
     * @param y 
     * @return int64_t g:ab
     */
    int64_t extgcd(int64_t a, int64_t b, int64_t& x, int64_t& y) {
        int64_t g = a;
        x         = 1;
        y         = 0;
        if (b != 0) g = extgcd(b, a % b, y, x), y -= (a / b) * x;
        return g;
    }

    /**
     * @brief u/d
     * @todo 
     * @tparam T 
     * @param u 
     * @param d 
     * @return T 
     */
    template <typename T>
    inline T ceil(T u, T d) {
        return (u + d - (T)1) / d;
