#include <stdio.h>

int N;
int input[100000];

int last(int K)
{
	int before = input[0] >= K ? 1 : 0;
	int new_input[100000]{}, new_input_cur = 0, cnt = 1;

	for (int i = 1; i < 2*N-1; i++)
	{
		int cur = input[i] >= K ? 1 : 0;

		if (before + cur == 1) cnt++;
		else
		{
			if (cnt % 2 == 1) new_input[new_input_cur++] = before;
			else
			{
				new_input[new_input_cur++] = 1 - before;
				new_input[new_input_cur++] = before;
			}
			cnt = 1;
		}
		before = cur;
	}
	if (cnt % 2 == 1) new_input[new_input_cur++] = before;
	else
	{
		new_input[new_input_cur++] = 1 - before;
		new_input[new_input_cur++] = before;
	}
	return new_input[(new_input_cur)/2];
}

int main()
{
	scanf("%d", &N);

	for (int i = 0; i < 2*N-1; i++) scanf("%d", &input[i]);

	int low = 1, high = 2 * N - 1;

	while (low <= high)
	{
		if (high - low > 1)
		{
			int mid = (low + high) / 2;

			if (last(mid) == 1) low = mid;
			else high = mid - 1;
		}
		else
		{
			if (high == low) break;
			else
			{
				if (last(high) == 1) low = high;
				break;
			}
		}
	}

	printf("%d\n", low);

	return 0;
}

