bool check(int limit){
	static int b[MAXN << 1];
	int n0 = n << 1;
	for (int i = 1; i < n0; ++i)
		b[i] = a[i] >= limit;

	int l, r;
	for (l = n; l > 1 && b[l] != b[l - 1]; --l);
	for (r = n; r < n0 - 1 && b[r] != b[r + 1]; ++r);
	if (l == 1 && r == n0 - 1)
		return b[1];
	if (l == n || r == n)
		return b[n];
	if (b[l] == b[r])
		return b[l];
	return n - l < r - n ? b[l] : b[r];
}

