int b[2*ee5];
bool check(int med){
  for(int i=0;i<n+n-1;i++)
    b[i]=(a[i]>=med);
  bool alternate=true;
  for(int i=1;i<n+n-1;i++){
    if(b[i]==b[i-1])
      {alternate=false;break;}
  }
  if(alternate)
    return b[0];
  /*for(int i=0;i<n+n-1;i++)
    cout<<b[i]<<" ";cout<<"\n";*/
  int ans=-1;
  int dist=INF;
  for(int j=0;j<n+n-1;){
    int x=b[j];
    int cnt=0;
    while(j<n+n-1 and x==b[j]){
      cnt++;j++;
    }
    int l=j-cnt;
    int r=j-1;
    //dbg(x,l,r);
 //   j to j+cnt-1
    if(cnt==1)continue;
    if(n-1>=l and n-1<=r){
      ans=x;
      dist=0;
    }
    else if(r<n-1){
      if(dist>n-1-r){
        ans=x;
        dist=n-1-r;
      }
    }
    else{
      if(dist>(l-n+1)){
        ans=x;
        dist=l-n+1;
      }
    }
  }
  return ans;
}

