bool ok(const vector<int>& muv, int v) {
	vector<bool> mub(muv.size());
	FOR(i, 0, muv.size()) mub[i] = (muv[i] >= v);
	int mid = mub.size() / 2 + 1;
	int d = mub.size()-1;
	FOR(i, mid, mub.size()-1) {
		if (mub[i] == mub[i + 1]) {
			d = i;
			break;
		}
	}
	RFOR(i, 1, mid+1) {
		if (mub[i] == mub[i - 1]) {
			d = (d - mid > i - mid ? i : d);
			break;
		}
	}
	return mub[d];
}


void solve() {
	int N = in();
	vector<int> A = in(N);
	int l = 2, h = N + 1;
	while (h - l > 1) {
		int mid = (h + l) / 2;
		if (ok(A, mid)) l = mid;
		else h = mid;
	}
	cout << l << endl;
}

