    if(b[mid]==b[mid+1]) return b[mid]==0?1:0; 

    while(lb>=2) {
        if(b[lb]!=b[lb+1] && b[lb]!=b[lb-1]) lb--;
        else break;
    }
    while(rb<2*n-1) {
        if(b[rb]!=b[rb-1] && b[rb]!=b[rb+1]) rb++;
        else break;
    }
    //printf("[%d, %d]\n", lb,rb);
    int R = min(mid-lb, rb-mid);
    if(lb==1 && rb==2*n-1) return (b[mid] ^ (R&1))==0?1:0;


    // [lb,mid,rb]

    if (mid-lb <= rb-mid) {
        int x1 = b[lb-1];    
