bool check(int mid){
    int l1 = 0, r1 = n * 2, l0 = 0, r0 = n * 2;
    if(a[0] >= mid){
        l1 = 1;
    }else{
        l0 = 1;
    }
    if(a[n * 2 - 2] >= mid){
        r1 = n * 2 - 1;
    }else{
        r0 = n * 2 - 1;
    }

    for(int i = n - 1 ; i > 0 ; --i){
        if(a[i] >= mid && a[i - 1] >= mid){
            l1 = i + 1;
            break;
        }else if(a[i] < mid && a[i - 1] < mid){
            l0 = i + 1;
            break;
        }
    }

    for(int i = n - 1 ; i < n * 2 - 2 ; ++i){
        if(a[i] >= mid && a[i + 1] >= mid){
            r1 = i + 1;
            break;
        }else if(a[i] < mid && a[i + 1] < mid){
            r0 = i + 1;
            break;
        }
    }

    return min(abs(n - l1), abs(n - r1)) < min(abs(n - l0), abs(n - r0));
}

int meguru_search(int ok, int ng){
    while(abs(ok - ng) > 1){
        int mid = (ok + ng) / 2;
        if(check(mid)){
            ok = mid;
        }else{
            ng = mid;
        }
    }
    return ok;
}

