            if(a[i] <= mid) test[i] = 1;
            else test[i] = 0;
        }
        bool checker = true;
        for(int i = 1; i <= 2*N-1; i+=2) {
            if(test[i] == 0) checker = false;
        }
        if(checker) {
            ok = mid;
            continue;
        }
        search(1);
        if(test2[N] == 1) ok = mid;
        else ng = mid;
    }
    cout << ok << endl;
    return 0;
}

